mapper.update:
UPDATE Note
SET title = #{title},description = #{desc},
created_on = #{createdOn}
WHERE note_id = #{noteId}

mapper.select:
SELECT
note_id,title, created_on,description FROM Note WHERE note_id =
#{noteId}

mapper.update:
UPDATE Note
SET
trash =
#{sentInTrash}
WHERE note_id = #{noteId}

UPDATE Note
SET
archive =
#{archived}
WHERE note_id = #{noteId}

UPDATE Note
SET
pinned =
#{pinned}
WHERE note_id = #{noteId}
